mapper.update:
    UPDATE tbl_reply
    SET  reply = #{reply}   
		,updatedate = sysdate
    WHERE rno = #{rno}

mapper.select:
SELECT * FROM ( SELECT /*+index_desc(tbl_reply PK_reply)*/ ROWNUM rn, rno, bno, reply, replyer, replyDate, updateDate FROM tbl_reply where bno = #{bno} and rno > 0
and ROWNUM <= #{cri.end}
) WHERE rn >= #{cri.start}


SELECT COUNT(rno) 
FROM tbl_reply
where bno = #{bno}
